import React, { useState } from "react";
import "./App.css";

function App() {
  const [location, setLocation] = useState("");
  const [weatherData, setWeatherData] = useState(null);

  const fetchWeather = (/* add parameters for api query */) => {
    if (location.trim() === "") {
      alert("Please enter a location");
      return;
    }

    const apiKey = "5a9d2c05e78649dba29133051232905";
    const apiUrl = `http://api.weatherapi.com/v1/`;

    let apiCall = `http://api.weatherapi.com/v1/current.json?key=${apiKey}&q=${location}&aqi=no`;

    const current = () => {
      apiCall = apiUrl + `current.json?key=${apiKey}&q=${location}&aqi=no`;
    };

    const future = () => {
      apiCall = apiUrl + `forecast.json?key=${apiKey}&q=${location}&aqi=no`;
    };

    fetch(apiCall)
      .then((response) => response.json())
      .then((data) => setWeatherData(current()))
      .catch((error) => {
        console.log("Error fetching weather data:", error);
      });
  };

  return (
    <html>
      <head></head>
      <body>
        <div id="root"></div>
        <div className="content-container">
          <div className="weather_search">
            <label htmlFor="location-input">Location: </label>
            <input
              type="text"
              id="location-input"
              value={location}
              onChange={(event) => setLocation(event.target.value)}
            />
            <button onClick={fetchWeather}>Get Weather</button>
          </div>

          {weatherData && (
            <div>
              <div className="content-1">
                <h2>{weatherData.location.name}</h2>
                <h1>{weatherData.current.temp_c}°C</h1>
                <h3>{weatherData.current.condition.text}</h3>
                <h4>
                  {weatherData.current.high_c}°C {weatherData.current.low_c}°C
                </h4>
              </div>

              <div className="weather_hourly">
                <div className="content-2">
                  <h2>Current hour</h2>
                  <img
                    className="weather-icon"
                    src="/images/icons8-cloud-lightning.gif"
                    alt=""
                  />
                  <h4>{weatherData.current.temp_c}°</h4>
                </div>

                <div className="content-2">
                  <h2>Current hour</h2>
                  <img
                    className="weather-icon"
                    src="/images/icons8-cloud-lightning.gif"
                    alt=""
                  />
                  <h4>{weatherData.current.temp_c}°</h4>
                </div>

                {weatherData && (
                  <div className="weather_hourly">
                    <div className="content-2">
                      <h2>Current hour</h2>
                      <img
                        className="weather-icon"
                        src="/images/icons8-cloud-lightning.gif"
                        alt=""
                      />
                      <h4>{weatherData.current.temp_c}°</h4>
                    </div>

                    {weatherData.forecast.forecastday[0].hour.map((hour) => (
                      <div className="content-2" key={hour.time}>
                        <h2>{hour.time}</h2>
                        <img
                          className="weather-icon"
                          src={`/images/${hour.condition.icon}`}
                          alt=""
                        />
                        <h4>{hour.temp_c}°</h4>
                      </div>
                    ))}
                  </div>
                )}

                {/* Render additional hourly weather data here */}
              </div>

              <div className="weather_day">
                <div className="content-3">
                  <h2>Location</h2>
                  <h1>Temp °</h1>
                  <h3>Conditions</h3>
                  <h4>high° low°</h4>
                  {/* Render 10-day weather summary here */}
                </div>
              </div>
            </div>
          )}
        </div>
      </body>
    </html>
  );
}

export default App;
