import React, { useState } from "react";
import "./App.css";

function App() {
  const [location, setLocation] = useState("Pretoria");
  const [weatherData, setWeatherData] = useState({
    current: null,
    forecast: null,
  });

  const apiKey = "5a9d2c05e78649dba29133051232905";
  const apiUrl = `http://api.weatherapi.com/v1/`;

  var current = (location) => {
    return apiUrl + `current.json?key=${apiKey}&q=${location}&aqi=no`;
  };

  var future = (location) => {
    return apiUrl + `forecast.json?key=${apiKey}&q=${location}&aqi=no`;
  };

  const fetchWeather = () => {
    if (location.trim() === "") {
      alert("Please enter a location");
      return "Cape Town";
    }

    let currentApiCall = current(location);
    let forecastApiCall = future(location);

    Promise.all([fetch(currentApiCall), fetch(forecastApiCall)])
      .then(([currentResponse, forecastResponse]) =>
        Promise.all([currentResponse.json(), forecastResponse.json()])
      )
      .then(([currentData, forecastData]) =>
        setWeatherData({ current: currentData, forecast: forecastData })
      )
      .catch((error) => {
        console.log("Error fetching weather data:", error);
      });
  };

  return (
    <div id="">
      <div className="content-container">
        <div className="weather_search">
          <label htmlFor="location-input">Location: </label>
          <input
            type="text"
            id="location-input"
            value={location}
            onChange={(event) => setLocation(event.target.value)}
          />
          <button onClick={fetchWeather}>Get Weather</button>
        </div>

        {weatherData && weatherData.current && (
          <div className="content-1">
            <h2>{weatherData.current.location.name}</h2>
            <h1>{weatherData.current.temp_c}°C</h1>
            <h3>{weatherData.current.condition.text}</h3>
            <h4>
              {weatherData.current.high_c}°C {weatherData.current.low_c}°C
            </h4>
          </div>
        )}

        {weatherData && weatherData.forecast && (
          <div className="weather_hourly">
            <div className="content-2">
              <h2>Current hour</h2>
              <img
                className="weather-icon"
                src="/images/icons8-cloud-lightning.gif"
                alt=""
              />
              <h4>{weatherData.current.temp_c}°</h4>
            </div>

            {weatherData.forecast.forecastday[0].hour.map((hour) => (
              <div className="content-2" key={hour.time}>
                <h2>{hour.time}</h2>
                <img
                  className="weather-icon"
                  src={`/images/${hour.condition.icon}`}
                  alt=""
                />
                <h4>{hour.temp_c}°</h4>
              </div>
            ))}
          </div>
        )}

        {weatherData && (
          <div className="weather_day">
            <div className="content-3">
              <h2>Location</h2>
              <h1>Temp °</h1>
              <h3>Conditions</h3>
              <h4>high° low°</h4>
              {/* Render 10-day weather summary here */}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
